        private void jRadioButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jRadioButton1ActionPerformed
                String secilen = evt.getActionCommand().toString();
		/*String secilenEleman = evt.getSource().toString();
		jTextArea1.setText(secilen + "\n" + secilenEleman);*/
		
		net.sirketadi.YardimciMetodlarDeposu anahtar = new net.sirketadi.YardimciMetodlarDeposu();
		
		if("Parametreli".equals(secilen)){
			String parametre = jTextField1.getText();
			String mesajiniz = anahtar.ugurlamaYap(parametre);
			jTextArea1.setText(mesajiniz);
		}else{
			String mesajiniz = anahtar.ugurlamaYap();
			jTextArea1.setText(mesajiniz);
		}
        }//GEN-LAST:event_jRadioButton1ActionPerformed

