        private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
               double en,boy,yukseklik,hacim,ucret;
	       
	       en = Double.parseDouble(jTextField1.getText());
	        boy = Double.parseDouble(jTextField2.getText());
		 yukseklik = Double.parseDouble(jTextField3.getText());
		  hacim = Double.parseDouble(jTextField4.getText());
		 ucret = Double.parseDouble(jTextField5.getText());
		
		BagajKutuUcreti nesne3 = new BagajKutuUcreti(ucret, hacim, en, boy, yukseklik);
		
		hacim = nesne3.hacimHesapla();
		
		jTextField4.setText(String.valueOf(hacim));
		
		ucret = nesne3.ucretHesapla();
				jTextField5.setText(String.valueOf(ucret));
        }//GEN-LAST:event_jButton1ActionPerformed

